mapper.select:
SELECT id,
code,
data,
data_type dataType,
date_time dateTime,
energy_type_id energyTypeId,
energys_base_type_id energysBaseTypeId,
project_id projectId,
structure_id structureId,
time,
update_time from aggregate_data


		and data_type = #{dataType}


		and time >= #{beginTime}


		and time <= #{endTime}


		and energy_type_id= #{energyTypeId}


		and structure_id= #{structureId}


		and energys_base_type_id= #{energysBaseTypeId}
